mapper.select:
SELECT c.ID,c.ASK,c.ANSWER,c.MEMBER_ID,c.STATUS,DATE_FORMAT(c.CREATE_DATE,'%Y-%m-%d %H:%i:%s') CREATE_DATE,c.CREATER_ID,c.QA_ORDER
FROM BX_QA c
WHERE 1=1
AND c.MEMBER_ID=#{memberId}
AND c.ask like CONCAT('%',#{ask},'%')
AND c.STATUS=0

    ORDER BY ${sortColumns}

LIMIT #{skip},#{pageSize}

SELECT c.ID,c.ASK,c.ANSWER,c.MEMBER_ID,c.STATUS,DATE_FORMAT(c.CREATE_DATE,'%Y-%m-%d %H:%i:%s') CREATE_DATE,c.CREATER_ID,c.QA_ORDER
FROM BX_QA c
WHERE c.id = #{id}
